pattern.template:
# 1. Add plugin to opencode.json
{
  "plugin": ["existing-plugin", "new-plugin@latest"]
}

# 2. Configure plugin settings
{
  "plugin_settings": {
    "new-plugin": {
      "enabled": true,
      "options": {}
    }
  }
}

# 3. Verify installation
opencode --version
opencode run "test" --model=plugin/model

# 1. Install authentication plugin
opencode plugin install auth-plugin

# 2. Authenticate
opencode auth login

# 3. Configure premium models
{
  "provider": {
    "google": {
      "models": {
        "premium-model": {
          "name": "Premium Model",
          "limit": { "context": 1048576, "output": 65536 }
        }
      }
    }
  }
}

# 1. Install skill via API
curl -sL "https://www.skillhub.club/api/v1/skills/skill-name/install?agents=opencode&format=sh" | bash

# 2. Configure in oh-my-opencode.json
{
  "skills": {
    "skill-name": {
      "enabled": true,
      "options": {}
    }
  }
}

# 3. Test skill functionality
opencode run "test skill" --skill=skill-name

